DataType.ClientEvent:
var property=arg.property;
if(property=="bankName"){
	var data = dsTbcm012.getData("#");
	if(data){
		var bankName = data.get("bankName");
		if(bankName=="中国银行"){
			data.set("bankHeadNo","BC");
		}else if(bankName=="中国农业银行"){
			data.set("bankHeadNo","ABC");
		}else if(bankName=="中国工商银行"){
			data.set("bankHeadNo","ICBC");
		}else if(bankName=="中国建设银行"){
			data.set("bankHeadNo","BBC");
		}else if(bankName=="交通银行"){
			data.set("bankHeadNo","TB");
		}else if(bankName=="中国邮政储蓄银行"){
			data.set("bankHeadNo","PBC");
		}else if(bankName=="中国民生银行"){
			data.set("bankHeadNo","MSBC");
		}else if(bankName=="平安银行"){
			data.set("bankHeadNo","PAB");
		}else if(bankName=="兴业银行"){
			data.set("bankHeadNo","XYB");
		}else if(bankName=="华夏银行"){
			data.set("bankHeadNo","HXB");
		}else if(bankName=="中信银行"){
			data.set("bankHeadNo","ZXB");
		}
	}
}

