functions = {
	"queryOnclick":function(data){

		dsTbcm011.set("parameter",data).flushAsync(function(returnData){
		//根据数据集的个数设置属性
		if(returnData.entityCount==0){
			//插入一行新数据
			dorado.MessageBox.alert("当前数据为空！");
		}else{
			//设置属性
		}
	});
},

