		}*/
	},
	
	"saveOrUpdDs":function(msg){
		debugger
		var state = dsTbcm011.getData("#").state;
		var dsTbcm011CD = dsTbcm011.getData("#");
		//验证新增必须参数
		if(msg=="新增" && state!=1){
		//复制新增
		 dsTbcm011CD.setState(1);
		 //将唯一数据置为空
		 dsTbcm011CD.set("updEmp",null);
		 dsTbcm011CD.set("updDate",null);
		 //其他视情况而定

		 dsTbcm011.insert(dsTbcm011CD);
	}

	if(msg=="修改" && state==0){
		
		dorado.MessageBox.alert("您没有要修改的数据！");
		return;
	}
	
	view.get("#saveTbcm011").set({
		executingMessage:"正在"+msg+"中 ... ",
		successMessage:msg+"已完成！"
	}).execute(function(){
		//刷新数据集，只显示当天新增数据
		dsTbcm011.set({
			//"createDate":new Date()
			//其他参数
		}).flushAsync();
	});
},

	"delOnClick":function(dataList){
		var section = dgTbcm011.get("selection").length;
		if(section==0){
			dorado.MessageBox.alert("请勾选要删除的数据！");
			return;
		}else{
			var options = {
				callback:function(){
					dataList.each(function(data){
						data.remove();
					});
					
					view.get("#saveTbcm011").set({
						executingMessage:"正在删除中 ... ",
						successMessage:"删除已完成！"
					}).execute();
				}
			};
			dorado.MessageBox.confirm("您确定要删除所选数据吗？",options);
		}
	},
	
	"queryCm012":function(data){
		debugger
		dsTbcm012.set("parameter",data).flushAsync(function(returnData){
		//根据数据集的个数设置属性
		if(returnData.entityCount==0){
			//插入一行新数据
			dorado.MessageBox.alert("当前数据为空！");
		}else{
			//设置属性
		}
	});
	
	},
	
	"saveOrUpdCm012":function(msg){
		var state = dsTbcm012.getData("#").state;
		var dsTbcm012CD = dsTbcm012.getData("#");
		//验证新增必须参数
		if(msg=="新增" && state!=1){
		//复制新增
		 dsTbcm012CD.setState(1);
		 //将唯一数据置为空
		 dsTbcm012CD.set("updEmp",null);
		 dsTbcm012CD.set("updDate",null);
		 //其他视情况而定

		 dsTbcm012.insert(dsTbcm012CD);
	}

	if(msg=="修改" && state==0){
		
		dorado.MessageBox.alert("您没有要修改的数据！");
		return;
	}
	
	view.get("#saveTbcm012").set({
		executingMessage:"正在"+msg+"中 ... ",
		successMessage:msg+"已完成！"
	}).execute(function(){
		//刷新数据集，只显示当天新增数据
		dsTbcm012.set("parameter",{
			compId:dsTbcm011.getData("#").get("compId"),
			custNo:dsTbcm011.getData("#").get("custNo")
			//其他参数
		}).flushAsync();
	});
	},
	
	"delCm012Onclick":function(dataList){
			var options = {
				callback:function(){
					dataList.each(function(data){
						data.remove();
					});
					
					view.get("#saveTbcm012").set({
						executingMessage:"正在删除中 ... ",
						successMessage:"删除已完成！"
					}).execute();
				},
				icon:"WARNING"
			};
			dorado.MessageBox.confirm("您确定要删除所选数据吗？",options);
	},
	
	"queryCustNo":function(data){
		view.get("#dsTbcm0111").set("parameter",data).flushAsync(function(returnData){
			if(returnData.entityCount==0){
				dorado.MessageBox.alert("当前数据为空！");
			}
		});
	}
	,
	//保存
	"saveOnClick":function(){
		var state = dsTbcm011.getData("#").state;
		var dataList = dgTbcm011.get("selection").slice(0);
		if(state==0){
			return;
		}else if(dsTbcm011.getData("#").validate("custNo") != "ok"){
			dorado.MessageBox.alert("客商编号为空，不能保存数据！");
			return;
		}else{
			view.get("#saveTbcm011").set({
				executingMessage:"正在保存中 ... ",
				successMessage:"保存已完成！"
			}).execute(function(){
				//是否刷新
				if(dataList){
					debugger
					dataList.removeAt(0);
					var len = dataList.length;
					if(len==0){
						view.get("#dialogTbcm011").close();
					}else{
						dsTbcm011.getData().setCurrent(dataList[0]);
						view.get("#dgTbcm011").set("selection",dataList);
					}
				}else{
				view.get("#dialogTbcm011").close();
				}
			});
		}
	},
	//保存银行信息
	"saveBankOnClick":function(){
		var state = dsTbcm012.getData("#").state;
		var dataList = dgTbcm012.get("selection").slice(0);
		if(state==0){
			return;
		}else if(dsTbcm012.getData("#").validate("bankNo") != "ok"){
			dorado.MessageBox.alert("银行代码为空，不能保存数据！");
			return;
		}else{
			view.get("#saveTbcm012").set({
				executingMessage:"正在保存中 ... ",
				successMessage:"保存已完成！"
			}).execute(function(){
				//是否刷新
				if(dataList){
					debugger
					dataList.removeAt(0);
					var len = dataList.length;
					if(len==0){
						view.get("#dialogTbcm012").close();
					}else{
						dsTbcm012.getData().setCurrent(dataList[0]);
						view.get("#dgTbcm012").set("selection",dataList);
					}
				}else{
				view.get("#dialogTbcm012").close();
				}
			});
		}
	}
}

