	self.set("selection",entity);
}

ControlTab.ClientEvent:
var dataList = dgTbcm011.get("selection").slice(0);
if(dataList.length>1){
	dorado.MessageBox.alert("一次只能查看一个客户的银行信息！",function(){
		view.get("#tabControl").set("currentTab","mainTab");
	});
	return;
}else{
if(dsTbcm011.getData("#") && dsTbcm011.getData("#").state==0){
	var compId = dsTbcm011.getData("#").get("compId");
	var custNo = dsTbcm011.getData("#").get("custNo");
	
	var data = {
		compId:compId,
		custNo,custNo
	};
	
	functions["queryCm012"](data);	
}else{
	dorado.MessageBox.alert("请选择主档信息！",function(){
		view.get("#tabControl").set("currentTab","mainTab");
	});
}
}

