Dialog.ClientEvent:
window.$selectIcon=function(e){
    x=e.pageX;
    y=e.pageY;
	if(isNaN(x)){
		x=e.clientX;
	}
	if(isNaN(y)){
		y=e.clientY;
	}
	x=x-parseInt($("#image1Icons").offset().left); 
    y=y-parseInt($("#image1Icons").offset().top);
	x=parseInt(x/20)*20;
	y=parseInt(y/20)*20;
	offsetStr=" -"+x+"px -"+y+"px";
	var bgStyle = "url(" + $('#image1Icons').attr('src')+ ")"+offsetStr;
	$("#selectedIconUrl").css("background",bgStyle);
	var url = "url(>/images/icon/icons.gif)"+offsetStr;
	window.$selectedIconUrl=url;
}

Button.ClientEvent:
var $selectedIconUrl=window.$selectedIconUrl;
if($selectedIconUrl){
	var callback=view.id("$dialogSelectIcon").callback;
	if(callback){
		var dialog=view.id("$dialogSelectIcon");
		callback.call(view,$selectedIconUrl);
		dialog.hide();
	}else{
		dorado.MessageBox.alert("调用图标选择窗口需要定义一个名为callback的回调函数！");
	}
}else{
	dorado.MessageBox.alert("请先选择一个图标！");
}
