Dialog.ClientEvent:
self.$show=function(config){
	if(!config.callback){
		dorado.MessageBox.alert("请先为当前dialog添加一个名为callback的函数");
		return;
	}
	self.callback=config.callback;
	if(config.width){
		self.set("width",config.width);
	}
	
	if(config.maxSelect){
		self.maxSelect=config.maxSelect;
	}
	
	if(config.height){
		self.set("height",config.height);
	}
	if(config.caption){
		self.set("caption",config.caption);
	}
	if(config.selected){
		self.selected=config.selected;
	}
	if(config.cache==undefined){
		self.cache=true;
	}else{
		self.cache=config.cache;
	}
	self.show();
}

Button.ClientEvent:
var data=view.id("$dataSetUserSelected").getData();
if(data.isEmpty()){
	dorado.MessageBox.alert("请选择一个用户后再进行此操作");
	return;
}
view.id("$dialogUserSelect").callback(data.toJSON());

DataSet.ClientEvent:
self.move=function(){
	var ds=view.id("$dataSetUserForSelect");
	var data=ds.getData("#");
	if(data){
		var targetData=view.id("$dataSetUserSelected").getData();
		var insertAble=true;
		targetData.each(function(user){
			if(user.get("username")==data.get("username")){
				insertAble=false;
				return false;
			}
		});
		if(insertAble){
			var max=view.id("$dialogUserSelect").maxSelect;
			if(max && targetData.toArray().length>=max){
				dorado.MessageBox.alert("当前最多只允许选择["+max+"]个用户！");
				return;
			}
			targetData.insert(data.toJSON());
			data.remove();
		}else{
			dorado.MessageBox.alert("当前用户已存在！")
		}
	}else{
		dorado.MessageBox.alert("当前没有选中的用户信息！")
	}	
}

var movedData=view.id("$dataSetUserSelected").getData();
var data=self.getData();
if(movedData){
	data.each(function(entity){
		movedData.each(function(movedEntity){
			if(entity.get("username")==movedEntity.get("username")){
				entity.remove();
			}
		});
	});
}
var dialog=view.id("$dialogUserSelect");
var selected=dialog.selected
if(selected){
	data.each(function(entity){
		for(var i=0;i<selected.length;i++){
			if(selected[i]==entity.get("username")){
				entity.remove();
				break;
			}
		}
	});
}

self.remove=function(){
	var data=view.id("$dataSetUserSelected").getData("#");
	if(data){
		data.remove();
		view.id("$dataSetUserForSelect").flushAsync();
	}else{
		dorado.MessageBox.alert("当前没有选中的用户信息！")
	}	
}
