DataPilot.ClientEvent:
var data=view.id("dataSetRole").getData("#");
if(!data){
	dorado.MessageBox.alert("请先选中一个角色!");
	arg.processDefault=false;
	return;
}
switch(arg.code){
	case "+":
		var dialog=view.id("$dialogUserSelect");	
		dialog.$show({
			caption:"选择用户",
			width:700,
			callback:function(users){
				var ids=[];
				for(var i=0;i<users.length;i++){
					var user=users[i];
					ids.push(user["username"]);
				}			
				var action=view.id("ajaxActionInsertMember");
				action.set("parameter",{ids:ids,type:"user",authorityType:"read",roleId:data.get("id")});
				action.execute(function(error){
					if(error){
						for(var i=0;i<users.length;i++){
							var user=users[i];
							if(user["username"]==error){
								if(user["cname"]){
									error=user["cname"];
								}
								break;				
							}
						}
						dorado.MessageBox.alert("当前选择的["+error+"]已添加，请重新选择");
					}else{
						dialog.hide();
						data.reset("users");
						view.id("$dataSetUserSelected").clear();
					}
				});
			}
		});
		arg.processDefault=false;
		break;
	case "-":
		dorado.MessageBox.confirm("真的要删除当前成员吗？",function(){
			var action=view.id("ajaxActionDeleteMember");
			action.set("parameter",view.id("dataSetRole").getData("#.#users.id"));
			action.execute(function(){
				data.reset("users");
			});		
		});
		arg.processDefault=false;
		break;
}

ToolBarButton.ClientEvent:
var data=view.id("dataSetRole").getData("#.users");
var members=[];
data.each(function(d){
	if(d.state==dorado.Entity.STATE_MODIFIED){
		d.state=dorado.Entity.STATE_NONE;
		members.push({memberId:d.get("id"),granted:d.get("granted")});
	}
});

if(members.length>0){
	var action=view.id("ajaxActionChangeGranted");
	action.set("parameter",{members:members});
	action.execute();
}else{
	dorado.MessageBox.alert("当前没有可供保存的数据");
}

DataColumn.ClientEvent:
var granted=arg.data.get("granted");
if(granted){
	arg.dom.innerHTML="<font color='green'>授权</font>";
}else{
	arg.dom.innerHTML="<font color='red'>不授权</font>";
}

